import React, { useState } from 'react';
import axios from 'axios';

const AddCustomer = () => {
  const [name, setName] = useState('');
  const [phone, setPhone] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:3010/addCustomer', { name, phone });
      alert('Customer added');
      setName('');
      setPhone('');
    } catch (err) {
      console.error(err);
      alert('Error adding customer');
    }
  };

  return (
    <div className="p-4">
      <h3 className="text-xl font-semibold mb-2">Add Customer</h3>
      <form onSubmit={handleSubmit} className="space-y-2">
        <input
          type="text"
          value={name}
          placeholder="Name"
          onChange={(e) => setName(e.target.value)}
          className="border px-2 py-1 w-full"
          required
        />
        <input
          type="text"
          value={phone}
          placeholder="Phone"
          onChange={(e) => setPhone(e.target.value)}
          className="border px-2 py-1 w-full"
          required
        />
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded">Add Customer</button>
      </form>
    </div>
  );
};

export default AddCustomer;
