import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AddTransaction = () => {
  const [customers, setCustomers] = useState([]);
  const [menuItems, setMenuItems] = useState([]);
  const [customer_id, setCustomerId] = useState('');
  const [amount, setAmount] = useState('');
  const [description, setDescription] = useState('');
  const [type, setType] = useState('credit');
  const [manualDescription, setManualDescription] = useState('');

  useEffect(() => {
    fetchCustomers();
    fetchMenuItems();
  }, []);

  const fetchCustomers = async () => {
    const res = await axios.get('http://localhost:3010/customers');
    setCustomers(res.data);
  };

  const fetchMenuItems = async () => {
    const res = await axios.get('http://localhost:3010/menu-items');
    setMenuItems(res.data);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const finalDescription = manualDescription.trim() || description;

    if (!finalDescription) {
      alert('Please select or enter a description');
      return;
    }

    try {
      await axios.post('http://localhost:3010/addTransaction', {
        customer_id,
        amount,
        description: finalDescription,
        type
      });
      alert('Transaction added');
      setAmount('');
      setDescription('');
      setManualDescription('');
    } catch (err) {
      console.error(err);
      alert('Error adding transaction');
    }
  };

  return (
    <div className="p-4 max-w-md mx-auto">
      <h3 className="text-xl font-bold mb-4">Add Transaction</h3>
      <form onSubmit={handleSubmit} className="space-y-3">
        <select
          value={customer_id}
          onChange={(e) => setCustomerId(e.target.value)}
          required
          className="border px-2 py-1 w-full rounded"
        >
          <option value="">Select Customer</option>
          {customers.map(cust => (
            <option key={cust.id} value={cust.id}>{cust.name}</option>
          ))}
        </select>

        <input
          type="number"
          value={amount}
          placeholder="Amount"
          onChange={(e) => setAmount(e.target.value)}
          required
          className="border px-2 py-1 w-full rounded"
        />

        <select
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          className="border px-2 py-1 w-full rounded"
        >
          <option value="">Select Menu Item</option>
          {menuItems.map(item => (
            <option key={item.id} value={item.name}>{item.name}</option>
          ))}
        </select>

        <input
          type="text"
          value={manualDescription}
          placeholder="Or enter description manually"
          onChange={(e) => setManualDescription(e.target.value)}
          className="border px-2 py-1 w-full rounded"
        />

        <select
          value={type}
          onChange={(e) => setType(e.target.value)}
          required
          className="border px-2 py-1 w-full rounded"
        >
          <option value="credit">Credit</option>
          <option value="debit">Debit</option>
        </select>

        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded w-full">
          Add Transaction
        </button>
      </form>
    </div>
  );
};

export default AddTransaction;
